import React, { Component } from "react";
import Col from "react-bootstrap/Col";
import NacionalService from "../../service/Nacional.service";

//import { Link } from "react-router-dom";

class NationalCard extends Component {
  constructor(props) {
    super(props);
    this._nationalService = new NationalService();
    this.state = {
      nacional: null,
      showModalWindow: false
    };
  }

  //state con la info de la card

  //cuando se monte llamar al servicio y actualizar el state
  componentDidMount = () => this.updateNationalList();

  updateNationalList = () => {
    this._nationalService
      .nacionalList()
      .then(nacional => this.setState({ nacional: nacional.data }))
      .catch(err => console.log("Error", err));
  };

  //pintar la carta con el state
  render() {
    <Col className="coaster-card" md={6}>
      <h4>Sorteo {this.state.fechaSorteo} </h4>
      <p> Número: {this.state.numero} </p>
      <p> Serie: {this.state.serie} </p>
      <p> Fracción: {this.state.fraccion} </p>
    </Col>;
  }
}

export default NationalCard;
