import React, { Component } from "react";
import Col from "react-bootstrap/Col";
import ResultService from "../../service/Juegos.service";

//import { Link } from "react-router-dom";

class JuegosBonolotoCard extends Component {
  constructor(props) {
    super(props);
    this._service = new ResultService();
    this.state = {
      bonoloto: null,
      showModalWindow: false
    };
  }

  //state con la info de la card

  //cuando se monte llamar al servicio y actualizar el state
  componentDidMount = () => this.updateBonolotoList();

  updateBonolotoList = () => {
    this._service
      .bonoloto()
      .then(bonoloto => this.setState({ bonoloto: bonoloto.data }))
      .catch(err => console.log("Error", err));
  };

  //pintar la carta con el state
  render() {
    return  (
      <Col className="coaster-card" md={5}>
        <h4 className="">BonoLotos pendientes</h4>
        {/* {this.state.bonoloto.map((bonoloto, index) => (
          <span key={index} className="">
            {" "}
            {bonoloto.fecha}{" "}
          </span>
        ))} */}

      </Col>
    ) 
  }
}


export default JuegosBonolotoCard;
