import React, { Component } from "react";
import { Container, Col, Row, Button } from "react-bootstrap";

class BonoLoto extends Component {
  constructor(props) {
    super(props);
    // this._service = new EuroService();
    this.state = {};
  }

  handleClick() {}
  handleInputChange = e => {
    let { name, value } = e.target;
    this.setState({
      nacional: { ...this.state.nacional, [name]: value }
    });
  };

  generateButtons(start, end) {
    let arrButtons1 = [];
    for (let i = start; i <= end; i++) {
      arrButtons1.push(
        <div variant="dark" type="submit" onClick={this.handleClick}>
          {i}
        </div>
      );
    }
    return arrButtons1;
  }

  render() {
    return (
      <Container>
        <section>
          <h1>BonoLoto</h1>
        </section>
        <Row>
          <Col xs={1}>
            <div variant="dark">1</div>
            {this.generateButtons(1, 9)}
          </Col>
          <Col xs={1}>{this.generateButtons(10, 19)}</Col>
          <Col xs={1}>{this.generateButtons(20, 29)}</Col>
          <Col xs={1}>{this.generateButtons(30, 39)}</Col>
          <Col xs={1}>{this.generateButtons(40, 49)}</Col>
        </Row>
      </Container>
    );
  }
}

export default BonoLoto;
