import React from "react";

import JuegosService from "../../service/Juegos.service";

import { Container, Row, Button, Modal } from "react-bootstrap";

import BonolotoOrderCard from "../../components/Juegos/JuegosBonoloto-card";

class JuegosOrder extends React.Component {
  constructor(props) {
    super(props);
    this._juegosService = new JuegosService();
    this.state = {
      bonotolo: [],
      otro: ""
    };
  }

  componentDidMount = () => this.updateNationalList();

  updateNationalList = () => {
    this._juegosService
      .bonoloto()
      .then(bonotolo => this.setState({ bonotolo: bonotolo.data }))
      .catch(err => console.log("Error", err));
  };
  // deleteHandler = id => {
  //   this._juegosService
  //     .deleteOrder(id)
  //     .then(x => this.updateNationalList())
  //     .catch(err => console.log("Error", err));
  // };

  render() {
    return (
      <section>
        <Container>
          <h1>Lista de pedidos:</h1>
          <Row>
            {this.state.nacional
              .map(nacional => (
                <NationalOrderCard
                  key={nacional._id}
                  {...nacional}
                  delete={this.deleteHandler}
                  updateNationalList={this.updateNationalList}
                />
              ))}
          </Row>
        </Container>
      </section>
    );
  }
}

export default JuegosOrder;
