import React, { Component } from "react";
import Col from "react-bootstrap/Col";

class NationalCard extends Component {
  constructor(props) {
    super(props);
    this._service = new ResultService();
    this.state = {
      primitiva: null,
      showModalWindow: false
    };
  }

  //state con la info de la card

  //cuando se monte llamar al servicio y actualizar el state
  componentDidMount = () => this.updatePrimitivaList();

  updatePrimitivaList = () => {
    this._service
      .primitiva()
      .then(euro => this.setState({ primitiva: euro.data }))
      .catch(err => console.log("Error", err));
  };

  //pintar la carta con el state
  render() {
    return (
      <Col className="coaster-card" md={6}>
        <h4>Sorteo {fechaSorteo} </h4>
        <p> Número: {numero} </p>
        <p> Serie: {serie} </p>
        <p> Fracción: {fraccion} </p>
        <p> Estado: {status} </p>
        <Button
          variant="dark"
          onClick={() => this.props.delete(this.props._id)}
        >
          Darlos de baja
        </Button>
      </Col>
    );
  }
}

export default NationalCard;
