import React, { Component } from "react";
import Col from "react-bootstrap/Col";
import ResultService from "../../service/Results.service";

//import { Link } from "react-router-dom";

class QuinielaCard extends Component {
  constructor(props) {
    super(props);
    this._service = new ResultService();
    this.state = {
      quiniela: null,
      showModalWindow: false
    };
  }

  //state con la info de la card

  //cuando se monte llamar al servicio y actualizar el state
  componentDidMount = () => this.updateEuromillonList();

  updateEuromillonList = () => {
    this._service
      .quiniela()
      .then(quiniela => this.setState({ quiniela: quiniela.data }))
      .catch(err => console.log("Error", err));
  };

  //pintar la carta con el state
  render() {
    return this.state.quiniela ? (
      <Col className="coaster-card" md={6}>
        <h4>Resultado del Quiniela del d√≠a</h4>
        {this.state.quiniela[0].map(quiniela => (
          <span className="numerosPrimitiva"> {quiniela} </span>
        ))}
        <span className="complementarioPrimi"> {this.state.quiniela[1]} </span>
        <span className="reintegroPrimitiva"> {this.state.quiniela[2]} </span>
        <span className="joker"> {this.state.quiniela[3]} </span>
      </Col>
    ) : (
      "Esperando resultados..."
    );
  }
}

export default QuinielaCard;
