import React, { Component } from "react";
import { Button, Form, Container , Row} from "react-bootstrap";

import { Link } from "react-router-dom";

import NationalService from "../../service/Nacional.service";

class BuyLottery extends Component {
  constructor(props) {
    super(props);
    this._nationalService = new NationalService();
    this.state = {
      otro: "",
      nacional: {

        fechaSorteo: ""
      }
    };
  }

  handleSubmit = e => {
    e.preventDefault();
    this._nationalService.postNacionalBuy(this.state.nacional);
  };

  handleInputChange = e => {
    let { name, value } = e.target;
    this.setState({
      nacional: { ...this.state.nacional, [name]: value }
    });
  };

  render() {
    return (
      <Container>
        <Form onSubmit={this.handleSubmit}>
          <br></br>
          <h2>Venta online de Lotería Nacional</h2>

          <Form.Group>
            <Form.Label>Fecha del sorteo: </Form.Label>
            <Form.Control
              type="text"
              name="numero"
              onChange={this.handleInputChange}
              value={this.state.nacional.fecha}
            />
            <Form.Label >Fecha del sorteo: </Form.Label>
            <Form.Control
              as={Row}
              type="text"
              name="numero"
              onChange={this.handleInputChange}
              value={this.state.nacional.fecha}
            />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº aleatorio pero iguales" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº aleatorio pero diferentes" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 0" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 1" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 2" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 3" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 4" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 5" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 6" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 7" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 8" />
          </Form.Group>
          <Form.Group>
            <Form.Check type="checkbox" label="Nº acabado en 9" />
          </Form.Group>

          <div className="botones">
            <Button variant="dark" type="submit">
              Pedir
            </Button>
            <Link to="/" className="btn btn-dark">
              Volver
            </Link>
          </div>
        </Form>
      </Container>
    );
  }
}

export default BuyLottery;
