import React from "react";

import NationalService from "../../service/Nacional.service";

import { Container, Row, Button, Modal } from "react-bootstrap";

// import CoasterCard from "./Coaster-card";
// import CoasterForm from "./Coaster-form";
import NationalOrderCard from "../../components/LoteriaNacional/NacionalOrder-Card";

class NationalOrder extends React.Component {
  constructor(props) {
    super(props);
    this._nationalService = new NationalService();
    this.state = {
      nacional: [],
      otro: ""
    };
  }

  componentDidMount = () => this.updateNationalList();

  updateNationalList = () => {
    this._nationalService
      .nacionalOrder()
      .then(nacional => this.setState({ nacional: nacional.data }))
      .catch(err => console.log("Error", err));
  };
  deleteHandler = id => {
    this._nationalService
      .deleteOrder(id)
      .then(x => this.updateNationalList())
      .catch(err => console.log("Error", err));
  };

  render() {
    return (
      <section>
        <Container>
          <h1>DÃ©cimos pedidos:</h1>

          <Row>
            {/* {this.state.nacional.map(nacional => (
              <NationalCard
                key={nacional._id}
                {...nacional}
                delete={this.deleteHandler}
                updateNationalList={this.updateNationalList}
              />
            )).sort()} */}
            {this.state.nacional.map(nacional => (
              <NationalOrderCard
                key={nacional._id}
                {...nacional}
                delete={this.deleteHandler}
                updateNationalList={this.updateNationalList}
              />
            ))}
          </Row>
        </Container>
      </section>
    );
  }
}

export default NationalOrder;
